package Day3;

public class CharDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//assinging sinle character literal
		char ch = 'a';
		System.out.println(ch);
		//assinging number to char
		char ch1 = 65;
		System.out.println(ch1);
		//assinging unicode to char
		char ch2 = '\u00A7';
		System.out.println(ch2);
		//assinging single character literal to int
		int a = 'a';
		System.out.println(a);
	}

}
